    while(k--)  
    {  
        char str[MAXSIZE];  
        cin >> str;  
        char stack[MAXSIZE];  
        int len = strlen(str);  
        int j = 0;  
        bool tag = true;  
        for(int i = 0; i < len; i++)  
        {  
            switch(str[i])  
            {  
            case '(' :  
            case '[' :   
                stack[j++] = str[i];  
                break;  
            case ')' :  
                if( stack[j - 1] == '(' )  
                {  
                    tag = true;  
                    j--;  
                }  
                else   
                    tag = false;  
                break;  
            case ']' :  
                if( stack[j - 1] == '[' )  
                {  
                    tag = true;  
                    j--;  
                }  
                else   
                    tag = false;  
                break;  
            default : cout << "Error!" << endl;  
            }  
            if(tag == false)    
                break;  
        }  
        if( tag == true && j == 0 )  
            cout << "Yes" << endl;  
        else  
            cout << "No" << endl;  
    }       
    return 0;  
}

