#include <iostream>
#include <cstdio>
#include <algorithm>
#include <cstring>
using namespace std;
const int MAXN=30003;
inline int L(int r){return r<<1;}
inline int R(int r){return ((r<<1)+1);}
inline int MID(int l,int r) {return (l+r)>>1;}
typedef struct
{
    int left,right;
    int value;
}LINE;
LINE tree[MAXN*4];
typedef struct NODE
{
    int value,zy;
    bool operator < (const NODE& r) const
    {
        return value<r.value;
    }
}NODE;
NODE node[MAXN];
int post[MAXN][2];
bool flag[MAXN];
int sum;
void Create(int l,int r,int root) 
{
    tree[root].left=l;
    tree[root].right=r;
    tree[root].value=0;
    if(l==r) return;
    int mid=MID(l,r);
    Create(l,mid,L(root));
    Create(mid+1,r,R(root));
}
void Update(int l,int r,int v,int root) 
{
    if(l<=tree[root].left&&tree[root].right<=r)
    {
        tree[root].value=v;
        return;
    }
    if(tree[root].left==tree[root].right) return;
    if(tree[root].value==v)return;
    if(tree[root].value>0)
    {
        tree[L(root)].value=tree[root].value;
        tree[R(root)].value=tree[root].value;
        tree[root].value=0;
    }
    int mid=MID(tree[root].left,tree[root].right);
    if(l>mid) Update(l,r,v,R(root));
    else if(r<=mid) Update(l,r,v,L(root));
    else
    {
        Update(l,mid,v,L(root));
        Update(mid+1,r,v,R(root));
    }
}
void solve(int r) 
{
    if(tree[r].value)
    {
        if(!flag[tree[r].value])
        {
            sum++;
            flag[tree[r].value]=1;
        }
        return;
    }
    solve(L(r));
    solve(R(r));
}
int main()
{
    int t;
    scanf("%d",&t);
    while(t--)
    {
        int n;
        scanf("%d",&n);
        for(int i=0;i<n;i++)
        {
            scanf("%d%d",&post[i][0],&post[i][1]);
            node[L(i)].zy=-i-1;
            node[R(i)].zy=i+1;
            node[L(i)].value=post[i][0];
            node[R(i)].value=post[i][1];
        }
        sort(node,node+n*2);
        int count=1,temp=node[0].value;
        for(int i=0;i<2*n;i++)
        {
            if(node[i].value!=temp)
            {
                count++;
                temp=node[i].value;
                if(i!=0)         
                if(node[i-1].zy>0)
                if((node[i-1].value+1)!=node[i].value)
                count++;
            }

            if(node[i].zy<0) post[-node[i].zy-1][0]=count;
            else post[node[i].zy-1][1]=count;
        }

        memset(flag,0,sizeof(flag));
        Create(1,count,1);
        for(int i=0;i<n;i++)
            Update(post[i][0],post[i][1],i+1,1);
        sum=0;
        solve(1);
        printf("%d\n",sum);

    }
    return 0;
}

