#include <iostream>  
#include <stdio.h>  
#include <algorithm>  
#include <math.h>  
using namespace std;  
struct sb  
{  
    double from,to;  
}a[10005];  
bool cmp(sb t1,sb t2)  
{  
    if(t1.from<t2.from)  
        return true;  
    if(t1.from==t2.from&&t1.to>t2.to)  
        return true;  
    return false;  
}  
int main()  
{  
    int t,n,i;  
    double d,h,r,w,x;  
    cin>>t;  
    while(t--)  
    {  
        int count=0,flag=0,sum=0;  
        double begin=0,end=0;  
        cin>>n>>w>>h;  
        for(i=0;i<n;i++)  
        {  
            cin>>x>>r;  
            d=sqrt(r*r-((h/2)*(h/2)));  
            if(r>=(h/2))  
            {  
                if(x-d>w||x+d<0)  
                    continue;  
                a[count].from=x-d;  
                a[count].to=x+d;  
                count++;  
            }  
        }  
        if(count==0)  
        {  
            cout<<0<<endl;  
            continue;  
        }  
        std::sort(a,a+count,cmp);  
        if(a[0].from>0)  
        {  
            cout<<0<<endl;  
            continue;  
        }  
        double max=0;  
        int num;  
        for(i=0;i<count;i++)  
        {  
            if(a[i].from<=end)  
            {  
                for(int num=i;num<count;num++)  
                {  
                    if(a[num].to>max&&a[num].to>end&&a[num].from<=end)  
                    {  
                        max=a[num].to;  
                        i=num;  
                    }  
                }  
                end=max;  
                sum++;  
            }  
            if(end>w)  
                break;  
        }  
        if(end<w||begin==end)  
        {  
            cout<<0<<endl;  
            continue;  
        }  
        cout<<sum<<endl;  
    }  
    return 0;  
}

