    }  
    return false;  
}  
int bfs()  
{  
    queue<Cup>q;  
    struct Cup tmp = {s.v[0], 0, 0};  
    tmp.step = 0;  
    q.push(tmp);  
    vis[s.v[0]][0][0] = true;  
    while(!q.empty())  
    {  
        Cup tmp = q.front(),now;  
        q.pop();  
        now = tmp;  
        if(now.v[0] == e.v[0] && now.v[1] == e.v[1] && now.v[2] == e.v[2])  
            return now.step ;  
        for(int i = 0; i < 3; i++)        
        {  
            for(int j = 0; j < 3; j++)  
            {  
                now = tmp;  
                if( i != j && del(now,i,j))  
                {  
                    now.step = tmp.step + 1;  
                    q.push(now);  
                }  
            }  
        }  
    }  
    return -1;  
     ;  
}  
