#include<stack>
using namespace std;
char ch[7][7]=
{
    {'>','>','<','<','<','>','>'},
    {'>','>','<','<','<','>','>'},
    {'>','>','>','>','<','>','>'},
    {'>','>','>','>','<','>','>'},
    {'<','<','<','<','<','=',' '},
    {' ',' ',' ',' ',' ',' ',' '},
    {'<','<','<','<','<',' ','='},
};
int sign(char ch)
{
    switch(ch)
    {
        case '+' : return 0;
        case '-'  : return 1;
        case '*'  : return 2;
        case '/'  : return 3;
        case '('  : return 4;
        case ')'  : return 5;
        case '=' : return 6;
    }
}
char pri(char a, char b)
{
    return ch[sign(a)][sign(b)];
}
double calc(double a, char oper, double b)
{
    switch(oper)
    {
        case '+' : return a + b;
        case '-' : return a - b;
        case '*' : return a * b;
        case '/' : return a / b;
    }
}
int main()
{
    int i,j,k,T;
    cin>>T;
    while(T--)
    {
        string str;
        str.clear();
        stack <double > num;
        stack <char> oper;
        cin>>str;
        oper.push('=');
        int d = 0;
        double temp;
        for(i=0;i<str.length();i++)
        {             
            if(isdigit(str[i])||str[i]=='.')
            {
                sscanf(&str[i],"%lf%n",&temp,&d);
                num.push(temp);
                i=i+d-1;
            }
            else if(!isdigit(str[i])&&str[i]!='.')
            {
                double a, b;
                char ope;
                switch(pri(oper.top(),str[i]))
                {
                    case '<' : oper.push(str[i]); break;
                    case '=' : oper.pop(); break;
                    case '>' : 
                        ope = oper.top(); oper.pop();
                        b = num.top(); num.pop();
                        a = num.top(); num.pop();
                        num.push(calc(a, ope, b));
                        i--;break;
                    default : break;
                }
            }
        }
        printf("%.2lf\n", num.top());
        while(!num.empty())
          num.pop();
        while(!oper.empty())
          oper.pop();

    }
    return 0;
}

