int gcd(int a,int b)  
{  
    if (a<b) 
    {  
        a = a^b;  
        b = a^b;  
        a = a^b;  
    }  
    while(b!=0)  
    {  
        a = a%b;  
        a = a^b;  
        b = a^b;  
        a = a^b;  
    }  
    return a;  
}

