const double e = 1e-6; 
double num[7],v;
int n;
bool dfs(int t)
{
    if(t==n)
    {
        if(fabs(v-num[n])<e)
            return true;
        return false;
    }
double left,right;
    for(int i=t;i<n;++i)
        for(int j=i+1;j<=n;++j)
        {
            left=num[i];
            right=num[j];
            num[i]=num[t];
            num[j]=left+right;    if(dfs(t+1)) return true;
            num[j]=left-right;    if(dfs(t+1)) return true;
            num[j]=left*right;  if(dfs(t+1)) return true;
