void multiply(int a[],int max,int b)  
{  
    int i,array=0;  
    for(i=max-1;i>=0;i--)  
    {  
        array+=b*a[i];  
        a[i]=array%base;  
        array/=base;  
    }  
}  
void add(int a[],int max,int b)  
{  
    int i,ad=0;  
    for(i=max-1;i>=0;i--)  
    {  
        a[i]+=b;  
        if(a[i]>=base)  
        {  
            a[i]%=base;  
            a[i-1]+=1;  
        }  
        else break;  
    }  
}  
