int mintime(int n,int *a)  
{  
    sort(a,a+n);  
    int res=0;  
    --n;  
    while(n>2)  
    {  
        res+=min(a[0]+a[1]*2+a[n],a[0]*2+a[n]+a[n-1]);  
        n-=2;  
    }  
    if(n==0)  
        res+=a[0];  
    else if(n==1)  
        res+=a[1];  
    else  
        res+=a[0]+a[1]+a[2];  
    return res;  
}  
