using namespace std;  
#define M 50005  
int en[M],res;  
int need[M][3];  
int searchSame[M];   
void judge(int n)  
{  
    en[1]=1;  
    for (int i=2;i<=n;i++)  
    {  
        if(need[ need[en[i-1]][1] ][0]==0)  
        {  
            en[i]=need[en[i-1]][1];  
            need[en[i-1]][0]=1;  
        }else if(need[ need[en[i-1]][2] ][0]==0)  
        {  
            en[i]=need[en[i-1]][2];  
            need[en[i-1]][0]=1;  
        }  
        else {res=-1; return;}  
    }  
    if( (need[en[n]][1]==en[1] || need[en[n]][1]==en[n-1])  
        && (need[en[n]][2]==en[1] || need[en[n]][2]==en[n-1]) )  
        return;  
    else res=-1;  
}  
int calculation(int n)  
{  
    int eqmax=0;  
    int temp;  
    memset(searchSame,0,sizeof(searchSame));  
    temp=0;  
    for (int i=1;i<=n;i++)  
    {  
        temp=(n+en[i]-i)%n;   
        searchSame[temp]++;  
    }  
    for (int i=0;i<=n;i++)  
        if(eqmax<searchSame[i]) eqmax=searchSame[i];  
    memset(searchSame,0,sizeof(searchSame));  
    for (int i=1;i<=n;i++)  
    {  
        temp=(n+en[n-i+1]-i)%n;   
        searchSame[temp]++;  
    }  
    for (int i=0;i<=n;i++)  
        if(eqmax<searchSame[i]) eqmax=searchSame[i];  
    return n-eqmax;  
}  
int main()  
{    
    int N,n;  
    scanf("%d",&N);  
    while(N--)  
    {  
        memset(need,0,sizeof(need));  
        memset(en,0,sizeof(en));  
        res=0;  
        scanf("%d",&n);  
        for (int i=1;i<=n;i++)  
        {  
            scanf("%d%d",&need[i][1],&need[i][2]);  
        }  
        judge(n);  
        if(res!=-1)  
            printf("%d\n",calculation(n));  
        else printf("-1\n");  
    }  
}

