int slove(int m,int n)
{
  int x1,y1,x2,y2,k;
  for(k=2;k<=m+n;++k)
   for(x1=1;x1<=m;++x1)
    for(x2=1;x2<=m;++x2)
     {
       y1=k-x1;
       y2=k-x2;
