bool flag;

void DFS(int i, int j);
void check();

void DFS(int i, int j)
{
    if(map[i][j] != 'X')
    {
        switch (map[i][j])
        {
            case 'a':
            case 'b':
            case 'c':
            case 'd':
            case 'e':
                ++findkeyOfDoor[map[i][j]-'a'];
                break;
            case 'A':
            case 'B':
            case 'C':
            case 'D':
            case 'E':
                q[map[i][j] - 'A'].x = i;
                q[map[i][j] - 'A'].y = j;
                ++q[map[i][j] - 'A'].num;
                return;
            case 'G':
                flag = true;
                return;
        }
        map[i][j] = 'X';  
        DFS(i-1, j);
        DFS(i+1, j);
        DFS(i, j-1);
        DFS(i, j+1);
        check();
    }
}

void check()
{
    for(int i = 0; i < 5; ++i)
    {
        if(q[i].num)
        {
            if(findkeyOfDoor[i] == maxkeyOfDoor[i])  
            {
                map[q[i].x][q[i].y] = 'X';
                DFS(q[i].x-1, q[i].y);
                DFS(q[i].x+1, q[i].y);
                DFS(q[i].x, q[i].y-1);
                DFS(q[i].x, q[i].y+1);
            }
        }
    }
}

