int fun(int n, int m)  
{
    if(n == 1 || m == 1)
        return 1;
    if(n < m)
        return fun(n, n);
    if(n > m)     
        return fun(n-m, m) + fun(n, m-1);
    if(n == m)
        return 1 + fun(n, m-1);
}

