int dir[4][2] = {-1, 0, 0, 1, 1, 0, 0, -1};

void BFS(int a, int b)
{
    queue <point> q;
    point t1, t2;
    t1.x = a;
    t1.y = b;
    q.push(t1);
    while(!q.empty())
    {
        t1= q.front();
        q.pop();
        for(int i = 0; i < 4; ++i)
        {
            t2.x = t1.x + dir[i][0];
            t2.y = t1.y + dir[i][1];
            if(t2.x < 0 || t2.x > h+1 || t2.y < 0 || t2.y > w+1 || map[t2.x][t2.y] == 0)
                continue;
            map[t2.x][t2.y] = 0;
            q.push(t2);
        }
    }
}

