int f()
{
    int x1,x2,ans=0,i;
    x1=x2=0;
    for(i=0;i<26;++i)
    {
        if(abs(degree_in[i]-degree_out[i])>=2)
            return -1;
        else if(degree_in[i]-degree_out[i]==1)
            x1++;
        else if(degree_in[i]-degree_out[i]==-1)
        {
            x2++;
            ans=i;
        }
    }
        if(x1>1||x2>1) 
            return -1;
        else if(x1==0)
        {
            for(i=0;i<26;++i)
                if(degree_out[i])
                    return i;
        }
        else
            return ans;

}
bool cmp(node a,node b)
{
    return strcmp(a.s,b.s)<0;
}

bool dfs(int st,int cnt)
{
    int i;
    if(cnt==m)
        return 1;
    for(i=0;i<m;++i)
    {
        if(a[i].first<st||used[i])
            continue;
        else if(a[i].first>st)
            return false;
        used[i]=true;
        order[cnt]=i;
        if(dfs(a[i].last,cnt+1))
            return 1;
        used[i]=false;
    }
    return false;
}

