            a[i].first =a[i].s[0]-'a';
            a[i].last =a[i].s[len-1]-'a';
            degree_out[a[i].s[0]-'a']++;
            degree_in[a[i].s[len-1]-'a']++;
        }
        start=f();
        if(start ==-1)
        {
            printf("***\n");
            continue;
        }
        sort(a,a+m,cmp);
