long long PowMod(long long a,long long p,long long m)  
{  
    long long result = 1;  
    while(p>0)  
    {  

        if (p%2)  
            result = (result*a)%m;  
        a = (a*a)%m;  
        p>>=1;  
    }  
    return result;  
}  
