#include <cstdio>
#include <cstring>
#include <iostream>

using namespace std;

const int N = 505;
int fight[N][N];
bool meet[N][N];

void init(int n)
{
    for(int i = 0; i < n; ++i)
        for(int j = 0; j < n; ++j)
            scanf("%d", &fight[i][j]);
    memset(meet, false, sizeof(meet));
    for(int i = 0; i < n; ++i)    
        meet[i][(i+1)%n] = true;
}

int solve(int n)
{
    for(int i = 1; i < n; ++i) 
    {
        for(int start = 0; start < n; ++start)
        {
            int end = (start + i + 1) % n;
            if(meet[start][end])
                continue;
            for(int t = (start + 1) % n; t != end; ++t, t%=n)   
            {                                         
                if(meet[start][t] && meet[t][end] && (fight[start][t] || fight[end][t]))
                {
                    meet[start][end] = true;
                    break;
                }
            }
        }
    }
    int cnt = 0;
    for(int i = 0; i < n; ++i)
    {
        if(meet[i][i])
            ++cnt;
    }
    return cnt;
}
int main()
{
    int T, n;
    scanf("%d", &T);
    while(T--)
    {
        scanf("%d", &n);
        init(n);
        printf("%d\n", solve(n));
    }
    return 0;
}

