    char s;
    while(scanf("%d/%d%c%d/%d",&a,&b,&s,&c,&d)!=EOF) {
        int m,n=b*d;
        a*=n/b;
        c*=n/d;
        if(s=='+')
            m=a+c;
        else {
            if(a<c) {
                printf("-");
                m=c-a;
            } else
                m=a-c;
        }
        if(!m)
            printf("0\n");
        else if(m%n==0) {
            printf("%d\n",m/n);
