#include<string.h>
using namespace std;
#define M 100100
#define N 5000
stack<int>s;

int n,m,t; 
int con,belong[N];
int head[N],next[M],to[M]; 
int dfn[N]; 
int low[N]; 
bool flag[N]; 
int min(int a,int b)
{
    return a<b?a:b;
}

void add(int u,int v)
{
    to[m]=v;
    next[m]=head[u];
    head[u]=m++;
}

void tarjan(int u)
{
    int v,p;
    t++;
    dfn[u]=low[u]=t;
    s.push(u);
    flag[u]=true;
    for(p=head[u];p>0;p=next[p])
    {
        v=to[p];
        if(dfn[v]==0)
        {
            tarjan(v); 
            low[u]=min(low[u],dfn[v]);
        }
        else if(flag[v]) 
            low[u]=min(low[u],dfn[v]);
    }
    if(dfn[u]==low[u]) 
    {
        int tmp=-1;
        con++;  
        while(tmp!=u)  
        {
            tmp=s.top();
            s.pop();
            belong[tmp]=con;
            flag[tmp]=false;
        }
    }
}

void solve()  
{
    for(int i=1;i<=n;++i)
        if(dfn[i]==0)
            tarjan(i);
}

void contract() 
{
    int innum=0,outnum=0,i,j;
    bool in[N],out[N];
    memset(in,0,sizeof(in));
    memset(out,0,sizeof(out));
    for(i=1;i<=n;i++)
        for(j=head[i];j>0;j=next[j])
            if(belong[i]!=belong[to[j]]) 
            {
                out[belong[i]]=1;
                in[belong[to[j]]]=1;
            }
    for(i=1;i<=con;++i)
    {
        if(in[i]==0)
            innum++;
        if(out[i]==0)
            outnum++;
    }
    if(con==1)
        printf("0\n");
    else
        printf("%d\n",innum>outnum?innum:outnum);
}

int main()
{
    int T,a;
    scanf("%d",&T);
    while(T--)
    {
        while(!s.empty())
            s.pop();
        memset(head,0,sizeof(head));
        memset(next,0,sizeof(next));
        memset(to,0,sizeof(to));
        memset(dfn,0,sizeof(dfn));
        m=1;
        t=0;
        con=0;
        scanf("%d",&n);
        for(int i=1;i<=n;++i)
        {
            while(scanf("%d",&a),a)
               add(i,a);
        }
        solve();
        contract();
    }
    return 0;
}

