#include<stdio.h>

const int N = 1000002;

struct TREE
{
	int Left;
	int Right;
	int num;
};
TREE tree[N<<2];

void Build(int root, int l, int r)
{
	tree[root].Left = l;
	tree[root].Right = r;
	tree[root].num = 0;
	if(l < r)
	{
		int m = (l + r)>>1;
		Build(root<<1, l, m);
		Build(root<<1|1, m + 1, r);
	}
}

void Insert(int root, int b, int e, int v)
{
	if(b <= tree[root].Left && tree[root].Right <= e)
	{
		tree[root].num += v;
		return;
	}
	int m = (tree[root].Left + tree[root].Right)>>1;
	if(e <= m)
		Insert(root<<1, b, e, v);
	else if(b > m)
		Insert(root<<1|1, b, e, v);
	else
	{
		Insert(root<<1, b, m, v);
		Insert(root<<1|1, m + 1, e, v);
	}
}

int Query(int root, int x)
{
	if(tree[root].Left == tree[root].Right)
		return tree[root].num;
	int m = (tree[root].Left + tree[root].Right)>>1;
	int sum = tree[root].num;
	if(x <= m)
		sum += Query(root<<1, x);
	else
		sum += Query(root<<1|1, x);
	return sum;
}

int main()
{
	int n,m;
	int b,e,v;
	char str[10];
	int i;
	while(scanf("%d %d", &n, &m) != EOF)
	{
		Build(1, 1, m);
		for(i = 0; i < n; ++i)
		{
			scanf("%s", str);
			if(str[0] == 'A')
			{
				scanf("%d %d %d", &b, &e, &v);
				Insert(1, b, e, v);
			}
			else
			{
				scanf("%d", &b);
				printf("%d\n", Query(1, b));
			}
		}
	}
	return 0;
}

