#include <stdlib.h>  
#include <vector>  

#define N 10010  

using namespace std;  

struct Node{  
    vector<int>father;  
    vector<int>son;  
};  

int fa[N];  
int ct;  

int getfather(int i)  
{  
    if(fa[i] == i) return i;  
    else return  fa[i] = getfather(fa[i]);  
}  

void merge(int a, int b)  
{  
    a = getfather(a);  
    b = getfather(b);  
    if(a == b) return;  
    else{  
        fa[b] = a;  
        ct --;  
    }  
}  

int main()  
{  
    int z = 0;  
    while(1){  
        ct = 0;  
        z ++;  
        Node *Nlist[N] = {};  
        int m, n;  
        scanf("%d%d", &m, &n);  
        if(m == -1 && n == -1) break;  
        int list[N];  
        int li = 0;  
        int ok;  
        for(int i = 0; i < N; i ++) fa[i] = i;  
        if(!(m == 0 && n == 0)){  
            while(1){  
                ct ++;  
                ok = 0;  
                merge(m, n);  
                for(int i = 0; i < li; i ++){  
                    if(list[i] == m){  
                        ok = 1;  
                        break;  
                    }  
                }  
                if(!ok) list[li++] = m;  
                ok = 0;  
                for(int i = 0; i < li; i ++){  
                    if(list[i] == n){  
                        ok = 1;  
                        break;  
                    }  
                }  

                if(!ok) list[li++] = n;  
                if(Nlist[m] == NULL) Nlist[m] = (Node *)calloc(1, sizeof(Node));  
                Nlist[m]->son.push_back(n);  
                if(Nlist[n] == NULL) Nlist[n] = (Node *)calloc(1, sizeof(Node));  
                Nlist[n]->father.push_back(m);         

                scanf("%d%d", &m, &n);  
                if(m == 0 && n == 0) break;  
            }  
            ok = 0;  
            int head = 0;  
            if(ct != 0) ok = 1;  
            if(!ok) for(int i = 0; i < li; i ++){  
                Node * nw = Nlist[list[i]];  
                if(nw ->father.size() == 0){  
                    head ++;  
                    if(head > 1){  
                        ok = 1;  
                        break;  
                    }  
                    continue;  
                }  
                if(nw ->father.size() > 1){  
                    ok = 1;  
                    break;  
                }  
                int father = nw ->father.at(0);  
                for(int t = 0; t < (int)nw ->son.size(); t++){  
                    if(father == nw ->son.at(t)){  
                        ok = 1;  
                        break;  
                    }  
                }  
                if(ok) break;  
            }  
            if(head == 0) ok = 1;  
        }  
        else ok = 0;  
        if(!ok) printf("Case %d is a tree.\n", z);  
        else printf("Case %d is not a tree.\n", z);  

        for(int i = 0; i < li; i ++){  
            free(Nlist[i]);  
        }  
    }  

    return 0;  
}

