struct snow  
{  
    int a[6];  
    struct snow *next;  
};  
snow *s[100005];  
bool fun(int b[],int sum)  
{  
    snow *p;p=s[sum]->next;  
    while(p)  
    {  
        for(int i=0;i<6;i++)  
        {  
            if(b[i]==p->a[0])  
            {  
                int j;  
                for(j=1;j<6;j++)  
                {  
                    if(b[(i+j)%6]!=p->a[j])break;  
                }  
                if(j==6)return true;  
                for(j=1;j<6;j++)  
                {  
                    if(b[((i-j)%6+6)%6]!=p->a[j])break;  
                }  
                if(j==6)return true;  
            }  
        }  
        p=p->next;  
    }  
    p=new snow;  
    for(int i=0;i<6;i++)  
    {  
        p->a[i]=b[i];  
    }  
    p->next=s[sum]->next;  
    s[sum]->next=p;  
    return false;  
}  
