#include<queue>  
#include<cstdio>  
#include<cmath>  
#include<iostream>  
using namespace std;  
const int M=1000001;  
int inf=1<<28;  

struct node{  
    node *next;  
    int num;  
    int x;  
    node(){  
        next=NULL;  
    }  
    node(int a){  
        x=a;  
        num=1;  
        next=NULL;  
    }  
};  
node no[M];  
void del(node * h){  
    if (h==NULL) return;  
    del(h->next);  
    delete h;  
}  
int t;  
int hash_size;  
bool hash1(int x){  
    t=x%M;  
    node *p=no[t].next;  
    while(p){  
        if (p->x ==x){  
            p->num++;  
            return 1;  
        }  
        else p=p->next;  
    }  
    p=new node(x);  
    p->next=no[t].next;  
    no[t].next=p;  
    hash_size++;  
    return 0;  
}  

bool hash2(int x){  
    node *p=no[x%M].next;  
    while(p){  
        if (p->x==x){  
            if (p->num>=2){  
                p->num--;  
                return 1;  
            }  
            else return 0;   
        }  
        else p=p->next;  
    }  
    return 0;  
}  

int main(){  
    int T,n,ans;  
    int x;  
    bool f;  
    scanf("%d",&T);  
    getchar();  
    while(T--){  
        for (int i=0;i<M;i++) {  
            del(no[i].next);  
            no[i].next=NULL;
        }  
        scanf("%d",&n);  
        queue<int> q;  
        ans=inf;  
        hash_size=0;  
        for (int i=0;i<n;i++){  
            scanf("%d",&x);  

            if (q.empty()) {  
                q.push(x);  
                no[x%M].next=new node(x);  
                ans=1;    
                hash_size++;  
            }  
            else {  
                f=hash1(x);  
                if (f){  
                    q.push(x);  
                    t=q.front();  
                    while(hash2(t)){  
                        q.pop();  
                        t=q.front();  
                    }  
                    ans= min(ans,(int)q.size());
                }  
                else {  
                    q.push(x);  
                    ans=q.size();
                }  
            }  
        }  
        printf("%d\n",ans);  
    }  
}

