#define FIB_NUM 62  

long long fib[FIB_NUM];    
int num[10050];    
bool res[520];    

void Build_Fib()                                        
{  
    fib[0] = 0;  
    fib[1] = 1;  
    for(int i = 2; i < FIB_NUM; i ++){  
        fib[i] = fib[i - 1] + fib[i - 2];  
    }  
}  

int main()    
{    
    int loop, ct;  
    Build_Fib();  
    scanf("%d", &loop);  
    while(loop --){  
        bool flag;  
        long long sum = 0, sum_back;                    
        int i, end_pos = 0;    
        memset(num, 0, sizeof(num));    
        memset(res, 0, sizeof(res));    

        flag = 0;  
        scanf("%d", &ct);  
        i = 2 * log( (double)ct ) / log(3.0) + 5;         
        while(ct){                                       
            scanf("%d", &num[i]);   
            if(!flag && !num[i]){                       
                continue;  
            }  

            flag = 1;  
            ct -= num[i];    
            i ++;    
        }   
        end_pos = i - 1;                                 

        i = 1;  
        while(++ i <= end_pos){                            
            sum += num[i] * fib[i];     
        }  
        sum_back = sum;  

        i = FIB_NUM - 1;    
        while(sum > 0){                                     
            if(sum >= fib[i]){    
                res[i] = 1;    
                sum -= fib[i];    
            }    
            i --;    
        }    

        i = 1;    
        flag = 0;  
        sum = sum_back;  
        while(sum) {                                 
            if(res[i]){  
                flag = 1;  
                sum -= fib[i];  
            }    
            if(flag)                                
                printf("%d ", res[i]);    
            i ++;    
        }    
        printf("\n");  
    }    

    return 0;    
}

