void Handle(){
	cin>>x>>y>>a>>b>>L;
	int t= gcd(a-b,L);
	if((y-x)%t!= 0){
		cout<<"Impossible"<<endl;
		return;
	}
	pair<lld,lld> result= Eculid(a-b,L,y-x);
	x= result.first%(L/t);
	if(x< 0 && L/t< 0){
		x-=L/t;
	}else if(x< 0 && L/t > 0){
		x+=L/t;
	}
	cout<<x<<endl;
}

pair<lld,lld> Eculid(lld a,lld b, lld left){
	if(!b){
		return make_pair<lld,lld>(left/a,0);
	}else{
		pair<lld,lld> base= Eculid(b,a%b,left);
		return make_pair<lld,lld>(-base.second,-(base.first+a/b*base.second));
	}
}

lld gcd(lld x, lld y){
	lld t;
	while(y){
		t= x;
		x= y;
		y= t%y;
	}
	return x;
}

