const int MAXN = 110;  

struct BigInt  
{  
    short s[MAXN*2];  
    int l;  
}c[MAXN];  

BigInt operator *(BigInt a, int b)  
{  
    int i;  
    for(i = 0; i < a.l; ++i)  
        a.s[i] *= b;  
    for(i = 0; i < a.l; ++i)  
    {  
        a.s[i+1] += a.s[i]/10;  
        a.s[i] %= 10;  
    }  
    while(a.s[a.l] != 0)  
    {  
        a.s[a.l+1] = a.s[a.l]/10;  
        a.s[a.l] %= 10;  
        a.l++;  
    }  
    return a;  
}  

BigInt operator /(BigInt a, int b)  
{  
    int i;  
    for(i = a.l-1; i > 0; --i)  
    {  
        a.s[i-1] += a.s[i]%b * 10;  
        a.s[i] /= b;  
    }  
    a.s[0] /= b;  
    while(a.s[a.l-1] == 0)  
        a.l--;  
    return a;  
}  

void Print(BigInt a)  
{  
    int i;  
    for(i = a.l-1; i >= 0; --i)  
        printf("%d", a.s[i]);  
    printf("\n");  
}  

