using namespace std;
struct node{
    node *next[10];
    int end;
    node(){  
        memset(next,NULL,sizeof(next));
        end=0;  
    }
};
node *root;
bool insert(char *s)
{
    int i,k,flag;
    node *p=root;
    for(flag=i=0;s[i];++i){
        k=s[i]-'0';
        if(p->next[k]==NULL){
            p->next[k]=new node();
            flag=1;   
        } 
        p=p->next[k];
        if(p->end) return 0; 
    }
    p->end=1;  
    if(!flag) return 0;
    return 1;
}
int del(node *p)  
{  
    if(p==NULL) return 0;
    for(int i=0;i<10;i++)
        if(p->next[i]) del(p->next[i]);
    delete p;
    return 0;
} 
