        bool flag=1;
        root=new node();
        scanf("%d",&n);
        while(n--){
            scanf("%s",s);
            if(flag) flag=insert(s);
        }
        del(root);
        if(flag) puts("YES");
        else puts("NO");
    }
    return 0;
}

