using namespace std;

string add(string a,string b)
{
    int i,j,k,flag;
    string c;
    c="";
    i=a.size()-1;j=b.size()-1;
    k=0;flag=0;
    while(i>=0&&j>=0)
    {
        c+=(a[i]+b[j]-'0'+flag);
        flag=0;
        if(c[k]>'9')
        {
            flag=1;
            c[k]=c[k]-10;
        }
        i--;j--;k++;
    }
    while(i>=0)
    {
        c+=(a[i]+flag);
        flag=0;
        if(c[k]>'9')
        {
            flag=1;
            c[k]=c[k]-10;
        }
        i--;k++;
    }
    while(j>=0)
    {
        c+=b[j]+flag;
        flag=0;
        if(c[k]>'9')
        {
            flag=1;
            c[k]=c[k]-10;
        }
        j--;k++;
    }
    char t;
    if(flag)
    {
        c+=(flag+'0');k++;
    }
    for(i=0,j=k-1;i<j;i++,j--)
    {
            t=c[i];c[i]=c[j];c[j]=t;
    }
    return c;
}

string mult(string a,string b)
{
    int flag=0,i,j,k,p,q,t,max;
    char ch;
    string c,ans;
    p=a.size()-1;q=b.size()-1;
    ans="0";
    for(i=p;i>=0;i--)
    {
        flag=0;c="";
        for(j=i;j<p;j++) c+='0';
        for(j=q;j>=0;j--)
        {
            t=(b[j]-'0')*(a[i]-'0')+flag;
            flag=t/10;
            c+=(t%10+'0');
        }
        if(flag) c+=(flag+'0');
        for(j=0,k=c.size()-1;j<k;j++,k--)
        {
            ch=c[j];c[j]=c[k];c[k]=ch;
        }
        ans=add(ans,c);
    }
    return ans;
}

