const int maxn = 30;  
const int N = 6;  
int dp[N][maxn];  
int digit[N], pos = 0;  
char s[N];  
int DP(int d, int num, int f)  
{  
    if(d == -1)  
        return 1;  
    if(!f && dp[d][num])  
        return dp[d][num];  
    int ans = 0, Max = f? digit[d] : 26;  
    for(int i = num? num+1:num; i <= Max; i++)  
        ans += DP(d-1, i, f&&(Max==i));  
    if(!f)  
        dp[d][num] = ans;  
    return ans;  
}  
int main()  
{  
    while(~scanf("%s", s))  
    {  
        int flag = 0;  
        pos = strlen(s);  
        pos--;  
        digit[0] = s[pos] - 'a' + 1;  
        for(int i = pos-1, j = 1; i >= 0; i--, j++)  
        {  
            if(s[i] >= s[i+1])  
                flag = 1;  
            digit[j] = s[i] - 'a' + 1;  
        }  
        if(flag)  
            puts("0");  
        else  
        {  
            int ans = DP(pos, 0, 1);  
            printf("%d\n", ans-1);  
        }  
    }  
    return 0;  
}

