const int N = 1005;  
const int M = 20;  
int cow[N];  
int dig[M];  
int n, m, k, ans;  
void inin()  
{  
    for(int i = 0; i <= 15; i++)  
        dig[i] = 1<<i;  
}  
void dfs(int i, int num, int sum)  
{  
    if(m-i+1 < k-num)
        return ;  
    if(num == k)  
    {  
        int tt = 0;  
        for(int j = 0; j < n; j++)  
            if(cow[j] == (cow[j]&sum))  
                tt++;  
        ans = max(ans, tt);  
        return ;  
    }  
    dfs(i+1, num, sum);  
    dfs(i+1, num+1, sum|=dig[i]);  
}  
int main()  
{  
    int T, x, t;  
    inin();  
    scanf("%d", &T);  
    while(T--)  
    {  
        scanf("%d%d%d", &n, &m, &k);  
        memset(cow, 0, sizeof(cow));  
        for(int i = 0; i < n; i++)  
        {  
            scanf("%d", &t);  
            while(t--)  
            {  
                scanf("%d", &x);  
                cow[i] |= dig[x];  
            }  
        }  
        ans = 0;  
        dfs(1, 0, 0);  
        printf("%d\n", ans);  
    }  
    return 0;  
}

