#include<stdio.h>
#include<string.h>
#include<iostream>
using namespace std;

const int maxM = 410;
char str[maxM][40], Key[maxM][40];
int stack[maxM], Hey[maxM], Kvar[maxM];
int num = 0;

int GetNum(char *var)
{
	for(int i = 0;i < num; ++ i)
		if(strcmp(var, Key[i]) == 0)
			return i;
	strcpy(Key[num], var);
	return num ++;
}
void GetChar(char *var,char *S)
{
	int i, len = strlen(S);
	for(i = 0;i < len; ++ i)
	{
		if(S[i] == ')' || S[i] == '=')break;
		var[i] = S[i];
	}
	var[i] = '\0';
}

int main()
{
	for(int i = 0;scanf("%s", str[i]) && str[i][0] != '$'; ++ i);
	int H = 0, top = 0, flag = 0;
	char var[30];
	while(true)
	{
		int len = strlen(str[H]), A, B;
		if(str[H][0] == '$')break;
		if(strcmp(str[H], "End") == 0 && flag)
			flag --;
		if(flag){
			if(str[H][len - 1] == ')')
				flag ++;
			H++;
			continue;
		}
		if(str[H][len - 1] == '+'){
			sscanf(str[H], "%[a-zA-Z0-9_]", var);
			A = GetNum(var);
			Kvar[A] ++;
		}
		else if(str[H][len - 1] == '0' && str[H][len - 2] == '=')
		{
			sscanf(str[H], "%[a-zA-Z0-9_]", var);
			A = GetNum(var);
			Kvar[A] = 0;
		}
		else if(strcmp(str[H], "show") == 0)
		{
			A = GetNum(str[++H]);
			printf("%d\n", Kvar[A]);
		}
		else if(str[H][len - 1] == ')')
		{
			GetChar(var, str[H] + 5);
			A = GetNum(var);
			if(Kvar[A] == 0)
			{
				flag = 1;
				H++;
				continue;
			}
			stack[top] = H;
			Hey[top ++] = Kvar[A];
		}
		else if(strcmp(str[H], "End") == 0)
		{
			if(-- Hey[top - 1] <= 0)
				top --;
			else
				H = stack[top - 1];
		}
		else
		{
			GetChar(var, str[H]);
			A = GetNum(var);
			int i = 0;
			for(;str[H][i] != '='; ++ i);
			i ++;
			GetChar(var, str[H] + i);
			B = GetNum(var);
			Kvar[A] = Kvar[B];
		}
		H ++;
	}
}

