using namespace std;   
const int maxn = 20020;  
int n, r[maxn], Left[maxn], Right[maxn];  

bool judge( int p )  
{  
    int x = r[1], y = p - r[1];  
    Left[1] = x; Right[1] = 0;  

    for(int i = 2; i <= n; i++ )  
    {  
        if(i % 2 == 0) 
        {  
            if(p - Left[i - 1] - Right[i - 1] < r[i] ) return false;  
            Left[i] = min(x - Left[i - 1], r[i]);  
            Right[i] = r[i] -Left[i];  
        }  
        else 
        {  
            if(p - Left[i - 1] - Right[i - 1] < r[i] ) return false;  
            Right[i] = min(r[i], y - Right[i - 1]);  
            Left[i] = r[i] - Right[i];  
        }  
    }  

    return Left[n] == 0;  
}  

int main()  
{  
    while( ~scanf("%d",&n) )  
    {  
        for(int i = 1; i <= n; i++ ) scanf("%d",&r[i]);  

        if(n == 1 ) { printf("%d\n", r[1]); continue;}  

        r[n + 1] = r[1];  

        int L = 0, R = 0;  
        for(int i = 1; i <= n; i++ ) L = max(L, r[i] + r[i + 1]);  

        if(n & 1)  
        {  
            for( int i = 1; i <= n; i++ ) R = max(R, r[i] * 3);  

            while( L < R )  
            {  
                int M = L + (R - L) / 2;  
                if( judge(M) ) R = M;  
                else L = M + 1;  

            }  

        }  
        printf("%d\n", L);  
    }  
    return 0;  
}

