using namespace std;

const int P = 105;
int map[P][P];
int dis[P];
int val[P];
int bag[1000005];

void dijkstra(int n)
{
    bool visit[n+5];
    memset(visit, false, sizeof(visit));
    visit[0] = true;
    for(int i = 0; i <= n; ++i)
        dis[i] = map[0][i];
    for(int i = 1; i < n; ++i)
    {
        int min = 1000000;
        int k = 0;
        for(int j = 0; j <= n; ++j)
        {
            if(!visit[j] && min > dis[j])
            {
                min = dis[j];
                k = j;
            }
        }
        visit[k] = true;
        for(int j = 0; j <= n; ++j)
        {
            if(!visit[j] && map[k][j] != 10000 && dis[j] > dis[k] + map[k][j])
                dis[j] = dis[k] + map[k][j];
        }
    }
}

int dp(int n, int vol)
{
    for(int i = 1; i <= n; ++i)
    {
        for(int j = vol; j >= dis[i]; --j)
        {
            if(bag[j] < bag[j-dis[i]] + val[i])
