const int N = 10005;  
int parent[N];  
bool IsDif[N];  
void Init(int n)  
{  
    for(int i = 1; i <= n; ++i)  
    {  
        parent[i] = i;  
        IsDif[i] = false;
    }  
}  
int Find(int x)  
{  
    if(x != parent[x])  
    {  
        int temp = parent[x];  
        parent[x] = Find(temp);  
        IsDif[x] ^= IsDif[temp];  
    }  
    return parent[x];  
}  
void Merge(int rx, int ry, int x, int y)  
{  
    if(rx < ry)  
    {  
        parent[ry] = rx;   
        IsDif[ry] = IsDif[x]^1^IsDif[y];  
    }  
    else  
    {  
        parent[rx] = ry;  
        IsDif[rx] = IsDif[x]^1^IsDif[y];  
    }  
}  
int main()  
{  
    int t,n,m,i;  
    int x,y,rx,ry;  
    bool flag;  
    int cnt = 0;  
    scanf("%d", &t);  
    while(t--)  
    {  
        scanf("%d %d", &n, &m);  
        Init(n);  
        flag = true;  
        for(i = 0; i < m; ++i)  
        {  
            scanf("%d %d", &x, &y);  
            if(flag)  
            {  
                if(x == y)  
                {  
                    flag = false;  
                    continue;  
                }  
                rx = Find(x);  
                ry = Find(y);  
                if(rx == ry)
                    flag = IsDif[x]^IsDif[y];  
                else  
                    Merge(rx, ry, x, y);  
            }  
        }  
        if(flag)  
            printf("Scenario #%d:\nNo suspicious bugs found!\n\n", ++cnt);  
        else  
            printf("Scenario #%d:\nSuspicious bugs found!\n\n", ++cnt);  
    }  
    return 0;  
}

