struct node  
{  
    int x,y;  
    int next;  
}edge[2*M];  
int idx,head[N];  
void Init(){ idx=0; memset(head,-1,sizeof(head)); }  
void Add(int x,int y)  
{  
    edge[idx].x=x;  
    edge[idx].y=y;  
    edge[idx].next = head[x];  
    head[x] = idx++;  
}  

bool mark[N];  

void BFS(int k)  
{  
    int i;  
    int x,y;  
    queue<int> q;  
    q.push(k);  
    mark[k] = true;  
    while(!q.empty())  
    {  
        x = q.front();  
        q.pop();  
        for(i=head[x]; i!=-1; i=edge[i].next)  
        {  
            y = edge[i].y;  
            if(!mark[y])  
                q.push(y);  
            mark[y] = true;  
        }  
    }  
    mark[k] = false;  
}  

struct point  
{  
    int x,y;  
}p[2*M];  

int num[N],num1[N],num2[N];  

int main()  
{  
    int n,m;  
