    {  
        Init();    
        for(i=0; i<m; ++i)  
        {  
            scanf("%d %d",&p[i].x,&p[i].y);  
            Add(p[i].x,p[i].y);  
        }  
        for(i=1; i<=n; ++i)  
        {  
            num1[i]=0;  
            memset(mark,false,sizeof(mark));  
            BFS(i);  
            for(j=1; j<=n; ++j)   
            {  
                if(mark[j])  
                    num1[i]++;   
            }  
        }  
        Init(); 
        for(i=0; i<m; ++i)  
            Add(p[i].y,p[i].x); 
        for(i=1; i<=n; ++i)   
        {  
            num2[i]=0;  
            memset(mark,false,sizeof(mark));  
            BFS(i);  
            for(j=1; j<=n; ++j)  
            {  
                if(mark[j])  
                    num2[i]++;  
            }  
        }  
        int ans = 0;  
        for(i=1; i<=n; ++i)  
        {  
            num[i] = num1[i]+num2[i];    
            if(num[i]==n-1)   
                ans++;  
        }  
        printf("%d\n",ans);  
    }  
    return 0;  
}

