const int N = 100000 + 10;

using namespace std;

int a[N], dp[N];

int binarysearch(int k, int len)
{
    int right = len;
    int left = 1;
    int mid = (right + left) >> 1;
    while(left <= right)
    {
        if(k == dp[mid])
            return mid;
        if(k > dp[mid])
            left = mid + 1;
        else
            right = mid - 1;
        mid = (right + left) >> 1;
    }
    return left;
}

