        len = 1;
        dp[1] = a[0];
        for(int i = 1; i < n; ++i)
        {
            t = binarysearch(a[i], len);
            dp[t] = a[i];
            if(t > len)
                len = t;
        }
        printf("%d\n", len);
    }
    return 0;
}

