#define MAX_SIZE 104  
#define INF 10000  
int n,m;  
int Time[MAX_SIZE][2];   
int dp[MAX_SIZE][MAX_SIZE];
int find_max(int a,int b)  
{  
    return a>b?a:b;  
}  
int check(int r)  
{  
    int i,j,k;  
    memset(dp,-1,sizeof(dp));  
    for(j=0;j<=m&&j*Time[1][0]<=r;j++)  
        dp[1][j]=(r-j*Time[1][0])/Time[1][1];  
    for(i=2;i<=n;i++)  
    {  
        for(j=0;j<=m;j++)  
        {  
            for(k=0;k<=j&&k*Time[i][0]<=r;k++)  
            { 
                if(dp[i-1][j-k]<0)  
                    continue;  
                int a_time=k*Time[i][0];  
                dp[i][j]=find_max(dp[i][j],dp[i-1][j-k]+(r-a_time)/Time[i][1]);  
            }  
        }  
        if(dp[i][m]>=m)  
            return 1;            
    }  
    return 0;  
}  
int solve()  
{  
    int lb=0,rb=INF;  
    while(rb-lb>=1)  
    {  
        int mid=(rb+lb)/2;  
        if(check(mid))  
            rb=mid;  
        else lb=mid+1;  
    }  
    return rb;  
}  
