#define max(a,b) (a>b?a:b)  
#define min(a,b) (a<b?a:b)  
#define N 100005  
int a[N],b[N],head1[N],head2[N],e;  
struct node  
{  
    int v,next;  
}bian[N*5];  
void add(int u,int v)  
{  
    bian[e].v=v;  
    bian[e].next=head1[u];  
    head1[u]=e++;  

    bian[e].v=u;  
    bian[e].next=head2[v];  
    head2[v]=e++;  
}  
int spfa(int s,int n)  
{  
    queue<int>q;  
    int x,i,v;  
    int mark1[N],mark2[N];  
    memset(mark1,0,sizeof(mark1));  
    memset(mark2,0,sizeof(mark2));  
    mark1[s]=1;  
    mark2[n]=1;  
    q.push(s);  
    while(!q.empty())  
    {  
        x=q.front();  
        q.pop();  
        for(i=head1[x];i!=-1;i=bian[i].next)  
        {  
            v=bian[i].v;  
            a[v]=min(a[v],a[x]);  
            if(!mark1[v])  
            {  
                mark1[v]=1;  
                q.push(v);  
            }  
        }  
    }  
    q.push(n);  
    while(!q.empty())       
    {  
        x=q.front();  
        q.pop();  
        for(i=head2[x];i!=-1;i=bian[i].next)  
        {  
            v=bian[i].v;  
            b[v]=max(b[v],b[x]);  
            if(!mark2[v])  
            {  
                mark2[v]=1;  
                q.push(v);  
            }  
        }  
    }  
    int ans=0;  
    for(i=1;i<=n;i++)  
    {  
        if(mark1[i]&&mark2[i])  
        {  
            ans=max(ans,b[i]-a[i]);  
        }  
    }  
    return ans;  
}  
int main()  
{  
    int n,m,u,v,x,i;  
    while(scanf("%d%d",&n,&m)!=-1)  
    {  
        for(i=1;i<=n;i++)  
        {  
            scanf("%d",&a[i]);  
            b[i]=a[i]; 
        }  
        memset(head1,-1,sizeof(head1));  
        memset(head2,-1,sizeof(head2));  
        e=0;  
        while(m--)  
        {  
            scanf("%d%d%d",&u,&v,&x);  
            add(u,v);  
            if(x==2)  
                add(v,u);  
        }  
        printf("%d\n",spfa(1,n));  
    }  
    return 0;  
}

