        for(i=0;i<m;i++)  
        {scanf("%d%d",&p[i].x,&p[i].y);}  
        qsort(p,m,sizeof(Node),com);
        v[count++]=p[0];  
        p[m].x=INF;p[m].y=INF;  
        for(i=1;i<m;i++)
        {  
            if(p[i].x==p[i-1].x)  
                continue;  
            if(p[i].y<p[0].y)  
                v[count++]=p[i];  
        }  
        for(i=m-1;i>=1;i--) 
        {  
            if(p[i].x==p[i+1].x)  
