        memset(str,0,sizeof(str));  
        scanf("%s",str);  
        len=strlen(str);  
        for(i=0;i<=len-1;i++)  
        {  
            if(str[i]>='0'&&str[i]<='9') 
            {  
                opnd.push(str[i]);  
            }  
            else  
            {  
                if(str[i]=='(')
                {  
                    optr.push('(');  
                }  
                else  
                {  
                    if(str[i]==')')
                    {  
                        while(optr.top()!='(')  
                        {  
                            opnd.push(optr.top());  
                            optr.pop();  
                        }  
                        optr.pop(); 
                    }  
                    else  
                    {  
                        if(optr.empty())
                        {  
                            optr.push(str[i]);  
                        }  
                        else  
                        {  
                            if(precede(str[i])>precede(optr.top())) 
                            {  
                                optr.push(str[i]);  
                            }  
                            else  
                            {  
                                while(!optr.empty()&&precede(str[i])<=precede(optr.top()))
                                {  
                                    opnd.push(optr.top());  
                                    optr.pop();  
                                }  
                                optr.push(str[i]);  
                            }  
                        }  
                    }  
                }  
            }  
        }  
        while(!optr.empty())
        {  
            opnd.push(optr.top());  
            optr.pop();  
        }  
        while(!opnd.empty())
        {  
            printf("%c",opnd.front());  
            opnd.pop();  
        }  
        printf("\n");  
    }  
    return 0;  
}

