#include <cstring>
#include <stack>
#include <cctype>

using namespace std;

char str[1005];
stack<char> Optr;
stack<float> Opnd;

char judge(char c1, char c2)
{
    if(c1 == '+' || c1 == '-')
    {
        if(c2 == '*' || c2 == '/' || c2 == '(')
            return '<';
        else
            return '>';
    }
    if(c1 == '*' || c1 == '/')
    {
        if(c2 == '(')
            return '<';
        return '>';
    }
    if(c1 == '(' && c2 == ')' || c1 == '=' && c2 == '=')
        return '=';
    return '<';
}

float cal(float num1, float num2, char p)
{
    if(p == '+')
        return num1 + num2;
    else if(p == '-')
        return num1 - num2;
    else if(p == '*')
        return num1 * num2;
    else if(p == '/')
        return num1 / num2;
}

void change()
{
    int len = strlen(str);
    while(!Opnd.empty())
        Opnd.pop();
    while(!Optr.empty())
        Optr.pop();
    Optr.push('=');

    for(int i = 0; i < len;)
    {
        if(isdigit(str[i]) || str[i] == '.')
        {
            float t1 = 0;
            int t2 = 1;
            bool flag = false;
            while(isdigit(str[i]) || str[i] == '.')
            {
                if(flag)
                    t2 *= 10;
                if(isdigit(str[i]))
                    t1 = t1*10+str[i]-'0';
                else
                    flag = true;
                printf("%c", str[i]);                
                ++i;
            }
            Opnd.push(t1/t2);
        }
        else
        {
            char tmp = judge(Optr.top(), str[i]);
            if(tmp == '<')
            {
                Optr.push(str[i]);
                ++i;
            }
            else if(tmp == '>')
            {
                tmp = Optr.top();
                printf("%c", tmp);
                Optr.pop();
                float num1 = Opnd.top();
                Opnd.pop();
                float num2 = Opnd.top();
                Opnd.pop();
                Opnd.push(cal(num2, num1, tmp));
            }
            else
            {
                Optr.pop();
                ++i;
            }
        }
    }
}

int main()
{
    int T;
    scanf("%d", &T);
    while(T--)
    {
        scanf("%s", str);
        change();
        printf("=\n%.2f\n", Opnd.top());
        if(T)
            printf("\n");
    }
    return 0;
}

