void func()  
{  
    int i,j,k;  
    for(i = 1; i < 10; i++)  
    {  
        dp[1][i] = 1;  
    }  
    for(i = 2; i < 10; i++)  
    {  
        for(j = 1; j <= 9*i; j++)  
        {  
            for(k = 0; k <= 9&&k < j; k++)  
            {  
                dp[i][j] += dp[i-1][j-k];  
            }  
        }  
    }  
}  
