int gcd(int n,int m)  
{   return m==0?n:gcd(m,n%m);  
}  
__int64 Polya(int Color,int num) 
{   __int64 sum=0;  
    for(int i=1;i<=num;i++)  
        sum+=pow(Color,gcd(num,i));  
    if(num&1) sum+=num*pow(Color,(num+1)/2);  
    else sum+=(pow(Color,num/2+1)+pow(Color,num/2))*num/2;        
    return sum/2/num;  
}  
