using namespace std;
int M, N;
int dx[] = {-1, 1, 0, 0};
int dy[] = {0, 0, -1, 1};
int map[310][310];
char str[310];
int ok, bx, by, ex, ey;
typedef struct
{
    int v;
    int step;
}State;
int vis[310][310];
priority_queue<State> q;
State s;
bool operator<(State a, State b)
{
    return a.step > b.step;
}
void bfs(int x, int y)
{
    int nx, ny, d; 
    s.v = x * N + y;
    vis[x][y] = 1;
    q.push(s);
    while(!q.empty())
    {
        State u = q.top();
        q.pop();
        x = u.v / N;
        y = u.v % N;
        for(d = 0; d < 4; d++)
        {
            nx = x + dx[d];
            ny = y + dy[d];
            if(nx >= 0 && nx < M && ny >= 0 && ny < N && !vis[nx][ny])
            {
                if(nx == ex && ny == ey)
                {
                    printf("%d\n", u.step+1);
                    ok = 1;
                    return;
                }
                else if(map[nx][ny] == 1)
                {
                    State v;
                    v.v = nx * N + ny;
                    v.step = u.step + 1;
                    vis[nx][ny] = 1;
                    q.push(v);
                }
                else if(map[nx][ny] == 'S' || map[nx][ny] == 'R')
                    continue;
                else if(map[nx][ny] == 'B')
                {
                    State v;
                    v.v = nx * N + ny;
                    v.step = u.step + 2;
                    vis[nx][ny] = 1;
                    q.push(v);
                }
            }
        }
    }
}
int main()
{
    int i, j;
    while(scanf("%d%d", &M, &N) && (M || N))
    {
        for(i = 0; i < M; i++)
        {
            scanf("%s", str);
            for(j = 0; j < N; j++)
            {
                switch(str[j])
                {
                case 'Y': bx = i; by = j; map[i][j] = 1; break;
                case 'T': ex = i; ey = j; map[i][j] = 1; break;
                case 'E': map[i][j] = 1; break;
                case 'B': map[i][j] = 'B'; break;
                case 'S': map[i][j] = 'S'; break;
