int stick[66], n, sum, len, ok, temp;  
bool vis[66];  

bool cmp(int a, int b){  
    return a > b;  
}  

bool DFS(int k, int left, int total){ 
    if(left == 0){ 
        total -= len;  
        if(!total) return true;  

        int i = 1;  
        while(vis[i]) ++i;  
        vis[i] = 1; 
        if(DFS(i + 1, len - stick[i], total)) return true;  
        vis[i] = 0;  
        return false;  
    }  

    for(int i = k; i < n; ++i){  
        if(i > 0 && stick[i] == stick[i-1] && !vis[i-1]) 
            continue;  
        if(!vis[i] && left >= stick[i]){           
            vis[i] = 1; left -= stick[i];   
            if(DFS(i + 1, left, total)) return true;  
            vis[i] = 0;   
            if(!left) return false; 
            left += stick[i];  
        }  
    }  
    return false;  
}  

