        }  
        sort(stick, stick + n, cmp);  
        temp = sum / 2; len = stick[0];  
        vis[0] = 1;  
        while(len <= temp){  
            if(sum % len == 0 && DFS(1, len - stick[0], sum)){ 
                printf("%d\n", len);   
                ok = 1; break;  
            }  
            ++len;  
        }  
        if(!ok) printf("%d\n", sum);  
    }  
    return 0;  
}

